import java.util.*;
import java.io.*;

public class test{
	public static final String fileName = "words.txt";

	public static void main(String[] args){
		//Open our file
		BufferedReader reader = null;
		try{
			reader = new BufferedReader(new FileReader(fileName));
			assert(reader != null);
			System.out.println((isDuplicates(reader))?"Duplicates exist":"Duplicates do not exist!");
		}catch (FileNotFoundException e){
			System.out.println("This file could not be located!");
		}catch (IOException e){
			System.out.println("Something went wrong reading the file");
		}


	}

    /**
     * Will determine if a file has duplicate Strings
     * @param reader the BufferedReader for the Strings to be read in from
     * @return true if duplicates exist, and false otherwise
     * @throws IOException if the file does not exist or cannot load
     */
	public static boolean isDuplicates(BufferedReader reader) throws IOException
	{
		//Create a map for the word count
		Map&lt;String, Integer&gt;  words = new HashMap<>();

		//Read each word
		String word = reader.readLine();
		while (word != null)
		{
			Integer success = words.putIfAbsent(word, 1);
			if (success != null)
			{
				return true;
			}
		}
		//otherwise
		return false;
	}

}

