public class SetExample {

    public static void main(String[] args) {
        GroupElement e0 = new GroupElement(0, 3);
        GroupElement e1 = new GroupElement(1, 3);
        GroupElement e2 = new GroupElement(2, 3);

        HashSet<GroupElement> group = new HashSet<>();
        group.add(e0);
        group.add(e1);
        group.add(e2);

        System.out.println("Let's find out some properties");
        System.out.println("Adding e0 again: "+ group.add(e0));
        System.out.println("Adding equivalent to e0: "+ group.add(new GroupElement(0, 3)));
        System.out.println("Adding a different equivalent to e0: "+group.add(new GroupElement(3, 3)));
        System.out.println("Cardinality (size) of group: "+ group.size());
        System.out.println("Removing e0: " + group.remove(e0));
        System.out.println("Cardinality (size) of group: "+ group.size());
        System.out.println("Readding e0: "+ group.add(e0));
        System.out.println("Removing equivalent to e0: " + group.remove(new GroupElement(0, 3)));
    }
}

