public class Penguin {
    private String n;
    public int A;

    public Penguin(String q, int R){
        this.n = q;
        this.A = R;
    }

    public static boolean FRIEndly(Penguin a, Penguin B){
        if (a.A - B.A >= 3){
            return false;
        }else if (B.A - a.A >= 3){
            return false;
        }
        return true;
    }
}

