from __future__ import print_function #This code will work on Python 2 and 3.


# Try changing these values to see what happens
x = 0
y = 5
z = 0
a = "Hello"
b = "World"
c = "Hello"
l1 = (x for x in range(100))
l2 = (x for x in range(100))


# NOTE: The 'is' keyword is nuanced. I would strongly suggest reading more
# about it before using it. There are some examples here.

if (x > y):
    print("{} is greater than {}".format(x, y))
else:
    print("{} is not greater than {}".format(x, y))

if (x == z):
    print("{} is numerically equal to {}".format(x, z))

if (x != y):
    print("{} and {} are not equal".format(x, y))

if (x is z):
    print("{} is {}".format(x, z))

x = z

if (x is z):
    print("{} is {} when assigned together".format(x, z))

if (a == c):
    print("{} and {} are equal".format(a, c))

if (a is c):
    print("{} is {}".format(a, c))

a = c

if (a is c):
    print("{} is {} after assignment".format(a, c))

if (list(l1) < list(l2)):
    print("l1 is < l2")

if (list(l2) > list(l1)):
    print("l2 is < l1")

if (l1 == l2):
    print("l1 and l2 are equal")

if (l1 is l2):
    print("l1 is l2")

l1 = l2

if (l1 is l2):
    print("l1 is l2 after assignment")
